import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Github, ChevronLeft, ChevronRight } from "lucide-react";
import dash from "../assets/dash.png";
import tic from "../assets/tictactoe.png";
import age from "../assets/agec.png";
import product from "../assets/product.png";
import counter from "../assets/counter.png";

// Sample project data

const projects = [
  {
    title: "Tic Tac Toe Game",
    description: "A simple Tic Tac Toe game built with React.",
    image: tic,
    github: "https://github.com/Abdul-Razack/Tic-Tac-Toe",

  },
  {
    title: "Dashboard",
    description: "Real-time dashboard using React and Tailwind CSS.",
    image: dash,
    github: "https://github.com/Abdul-Razack/dashboard",

  },
  {
    title: "Age Calculator",
    description: "A simple age calculator built with Js.",
    image: age,
    github: "https://github.com/Abdul-Razack/Age-Calculator",

  },
  {
    title: "Product Catalog",
    description: "A simple product catalog built with React.",
    image: product,
    github: "https://github.com/Abdul-Razack/Product-Catalog",

  },
  {
    title: "Counter App",
    description: "A simple counter app built with React.",
    image: counter,
    github: "https://github.com/Abdul-Razack/mini-project-counter-app-react",

  }
];

export default function Projects() {
  const staticProjects = projects.slice(0, 3); 
  const sliderProjects = projects.slice(3);
  const [index, setIndex] = useState(0);

  const next = () => setIndex((prev) => (prev + 1) % sliderProjects.length);
  const prev = () => setIndex((prev) => (prev - 1 + sliderProjects.length) % sliderProjects.length);

  return (
    <section id="projects" className="px-4 text-center">
      <h2 className="text-3xl font-bold mb-8 text-gray-900 dark:text-white">
        Projects
      </h2>

      <div className="relative max-w-xl mx-auto">
        <AnimatePresence mode="wait">
          <motion.div
            key={index}
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -100 }}
            transition={{ duration: 0.5 }}
            className="rounded-2xl overflow-hidden shadow-lg bg-gradient-to-b from-gray-100 to-gray-200 dark:from-gray-800 dark:to-gray-900"
          >
            <img
              src={projects[index].image}
              alt={projects[index].title}
              className="w-full h-48 object-cover"
            />
            <div className="p-6">
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                {projects[index].title}
              </h3>
              <p className="text-gray-700 dark:text-gray-300 mb-4">
                {projects[index].description}
              </p>
              <a
                href={projects[index].github}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center gap-1 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded hover:bg-gray-200 dark:hover:bg-gray-700 transition"
              >
                <Github className="w-4 h-4" /> Code
              </a>
            </div>
          </motion.div>
        </AnimatePresence>

        {/* Navigation buttons */}
        <button
          onClick={prev}
          className="absolute top-1/2 -left-8 transform -translate-y-1/2 bg-gray-200 dark:bg-gray-700 p-2 rounded-full hover:scale-110 transition"
        >
          <ChevronLeft className="w-5 h-5 text-gray-800 dark:text-white" />
        </button>
        <button
          onClick={next}
          className="absolute top-1/2 -right-8 transform -translate-y-1/2 bg-gray-200 dark:bg-gray-700 p-2 rounded-full hover:scale-110 transition"
        >
          <ChevronRight className="w-5 h-5 text-gray-800 dark:text-white" />
        </button>
      </div>

      {/* Dots indicator */}
      <div className="flex justify-center mt-4 space-x-2">
        {projects.map((_, i) => (
          <span
            key={i}
            className={`w-3 h-3 rounded-full ${
              i === index ? "bg-blue-500" : "bg-gray-400"
            }`}
          ></span>
        ))}
      </div>
    </section>
  );
}
