import { motion } from "framer-motion";
import { Download } from "lucide-react";
import pdf from "../assets/ABDUL RAZACK A Resume.pdf";

export default function WorkExperience() {
  return (
    <motion.section
      id="experience"
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.6 }}
      className="text-center max-w-3xl mx-auto"
    >
      <h2 className="text-3xl font-bold mb-6 text-gray-900 dark:text-white">
        Work Experience
      </h2>

      <div className="text-left mb-6">
        <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
          Front-End Developer at <span className="text-blue-600">Numerique360</span>
        </h3>
        <p className="text-gray-700 dark:text-gray-300 mb-2">
          Duration: <span className="font-medium">[Start Date] - [End Date]</span>
        </p>
        <ul className="list-disc list-inside text-gray-700 dark:text-gray-300 leading-relaxed">
          <li>Developed responsive web applications using <span className="font-medium">React.js, Tailwind CSS, and JavaScript</span>.</li>
          <li>Integrated <span className="font-medium">RESTful APIs</span> to fetch and display dynamic data.</li>
          <li>Ensured high performance and accessibility across web applications.</li>
          <li>Collaborated with designers and back-end developers to deliver seamless UI/UX experiences.</li>
        </ul>
      </div>

      <a
        href={pdf}
        download
        className="inline-flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700 transition"
      >
        <Download className="w-5 h-5" />
        Download Resume
      </a>
    </motion.section>
  );
}
