import { useRef } from "react";
import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Github, ChevronLeft, ChevronRight } from "lucide-react";
import dash from "../assets/dash.png";
import tic from "../assets/tictactoe.png";
import age from "../assets/agec.png";
import product from "../assets/product.png";
import counter from "../assets/counter.png";

// Sample project data

const projects = [
  {
    title: "Tic Tac Toe Game",
    description: "A simple Tic Tac Toe game built with React.",
    image: tic,
    github: "https://github.com/Abdul-Razack/Tic-Tac-Toe",

  },
  {
    title: "Dashboard",
    description: "Real-time dashboard using React and Tailwind CSS.",
    image: dash,
    github: "https://github.com/Abdul-Razack/dashboard",

  },
  {
    title: "Age Calculator",
    description: "A simple age calculator built with Js.",
    image: age,
    github: "https://github.com/Abdul-Razack/Age-Calculator",

  },
  {
    title: "Product Catalog",
    description: "A simple product catalog built with React.",
    image: product,
    github: "https://github.com/Abdul-Razack/Product-Catalog",

  },
  {
    title: "Counter App",
    description: "A simple counter app built with React.",
    image: counter,
    github: "https://github.com/Abdul-Razack/mini-project-counter-app-react",

  }
];

export default function Projects() {
  const sliderRef = useRef(null);
  const [disablePrev, setDisablePrev] = useState(true);
  const [disableNext, setDisableNext] = useState(false);

  const cardWidth = 300 + 24; // width + gap

  const updateButtons = () => {
    const container = sliderRef.current;
    if (!container) return;

    setDisablePrev(container.scrollLeft <= 0);

    const maxScrollLeft = container.scrollWidth - container.clientWidth - 5; // small buffer
    setDisableNext(container.scrollLeft >= maxScrollLeft);
  };

  useEffect(() => {
    updateButtons();
    const container = sliderRef.current;
    container.addEventListener("scroll", updateButtons);
    return () => container.removeEventListener("scroll", updateButtons);
  }, []);

  const scrollSlider = (dir) => {
    const container = sliderRef.current;
    if (!container) return;
    const amount = dir === "next" ? cardWidth : -cardWidth;
    container.scrollBy({ left: amount, behavior: "smooth" });
  };

  return (
    <section id="projects" className="px-4">
      <h2 className="text-3xl font-bold text-center mb-12 text-gray-900 dark:text-white">
        Projects
      </h2>

      <div className="relative max-w-6xl mx-auto">
        {/* Left button */}
        <button
          onClick={() => scrollSlider("prev")}
          disabled={disablePrev}
          className={`absolute -left-4 top-1/2 z-10 -translate-y-1/2 p-2 rounded-full transition 
            ${disablePrev ? "opacity-40 cursor-not-allowed" : "bg-gray-200 dark:bg-gray-700 hover:scale-110"}`}
        >
          <ChevronLeft className="w-5 h-5" />
        </button>

        {/* Slider container */}
        <div
          ref={sliderRef}
          className="flex gap-6 overflow-x-auto scroll-smooth no-scrollbar px-4"
        >
          {projects.map((p, i) => (
            <motion.div
              key={i}
              whileHover={{ scale: 1.05, y: -5 }}
              className="min-w-[300px] rounded-2xl overflow-hidden shadow-lg bg-gradient-to-b from-gray-100 to-gray-200 dark:from-gray-800 dark:to-gray-900"
            >
              <img src={p.image} alt={p.title} className="w-full h-48 object-cover" />
              <div className="p-6">
                <h3 className="text-xl font-semibold mb-2 text-gray-900 dark:text-white">
                  {p.title}
                </h3>
                <p className="text-gray-700 dark:text-gray-300">{p.description}</p>
                <a
                  href={p.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="mt-4 inline-flex items-center gap-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded hover:bg-gray-200 dark:hover:bg-gray-700 transition"
                >
                  <Github className="w-4 h-4" /> Code
                </a>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Right button */}
        <button
          onClick={() => scrollSlider("next")}
          disabled={disableNext}
          className={`absolute -right-4 top-1/2 z-10 -translate-y-1/2 p-2 rounded-full transition 
            ${disableNext ? "opacity-40 cursor-not-allowed" : "bg-gray-200 dark:bg-gray-700 hover:scale-110"}`}
        >
          <ChevronRight className="w-5 h-5" />
        </button>
      </div>
    </section>
  );
}
