import { motion } from "framer-motion";

export default function MoreAboutMe() {
  return (
    <motion.section
      id="more-about-me"
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.6 }}
      className="py-12 bg-white rounded-xl shadow-2xl"
    >
      <div className="max-w-5xl mx-auto px-4">
        <h2 className="text-3xl font-bold mb-8 text-center text-gray-900">
          More About Me
        </h2>

        <div className="space-y-6 text-gray-700 text-lg md:text-xl leading-relaxed">
          <motion.p
            initial={{ opacity: 0, x: -20 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5 }}
          >
            I am a passionate web developer with a strong foundation in front-end technologies. I love creating interactive and user-friendly web applications that provide a seamless experience.
          </motion.p>

          <motion.p
            initial={{ opacity: 0, x: 20 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            In my free time, I enjoy exploring new technologies, contributing to open-source projects, and sharing my knowledge with the developer community.
          </motion.p>

          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: 0.4 }}
          >
            I am always eager to learn and grow as a developer, and I am excited about the opportunities that lie ahead.
          </motion.p>
        </div>
      </div>
    </motion.section>
  );
}
