import { useRef } from "react";
import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Github, ChevronLeft, ChevronRight } from "lucide-react";
import dash from "../assets/dash.png";
import tic from "../assets/tictactoe.png";
import age from "../assets/agec.png";
import product from "../assets/product.png";
import counter from "../assets/counter.png";

// Sample project data

const projects = [
  {
    title: "Tic Tac Toe Game",
    description: "A simple Tic Tac Toe game built with React.",
    image: tic,
    github: "https://github.com/Abdul-Razack/Tic-Tac-Toe",

  },
  {
    title: "Dashboard",
    description: "Real-time dashboard using React and Tailwind CSS.",
    image: dash,
    github: "https://github.com/Abdul-Razack/dashboard",

  },
  {
    title: "Age Calculator",
    description: "A simple age calculator built with Js.",
    image: age,
    github: "https://github.com/Abdul-Razack/Age-Calculator",

  },
  {
    title: "Product Catalog",
    description: "A simple product catalog built with React.",
    image: product,
    github: "https://github.com/Abdul-Razack/Product-Catalog",

  },
  {
    title: "Counter App",
    description: "A simple counter app built with React.",
    image: counter,
    github: "https://github.com/Abdul-Razack/mini-project-counter-app-react",

  }
];

export default function Projects() {
  const [index, setIndex] = useState(0);
  const visibleCount = 3;

  const nextSlide = () => {
    if (index < projects.length - visibleCount) setIndex(index + 1);
  };

  const prevSlide = () => {
    if (index > 0) setIndex(index - 1);
  };

  return (
    <section id="projects" className="px-4">
      <h2 className="text-3xl font-bold text-center mb-8 text-gray-900 dark:text-white">
        Projects
      </h2>

      <div className="relative max-w-6xl mx-auto">
        {/* Buttons */}
        <button
          onClick={prevSlide}
          className="absolute -left-5 top-1/2 z-10 -translate-y-1/2 bg-gray-200 dark:bg-gray-700 p-2 rounded-full hover:scale-110 transition disabled:opacity-40"
          disabled={index === 0}
        >
          <ChevronLeft className="w-5 h-5" />
        </button>

        <div className="overflow-hidden">
          <motion.div
            className="flex gap-6"
            animate={{ x: `-${index * (300 + 24)}px` }} // 300 card + 24 gap
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
          >
            {projects.map((p, i) => (
              <div
                key={i}
                className="w-[300px] flex-shrink-0 rounded-2xl overflow-hidden shadow-lg bg-gradient-to-b from-gray-100 to-gray-200 dark:from-gray-800 dark:to-gray-900"
              >
                <img src={p.image} alt={p.title} className="w-full h-40 object-cover" />
                <div className="p-4">
                  <h3 className="text-lg font-semibold mb-2 text-gray-900 dark:text-white">
                    {p.title}
                  </h3>
                  <p className="text-gray-700 dark:text-gray-300 text-sm">{p.description}</p>
                  <a
                    href={p.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mt-3 inline-flex items-center gap-1 px-3 py-1 border border-gray-300 dark:border-gray-600 rounded hover:bg-gray-200 dark:hover:bg-gray-700 transition text-sm"
                  >
                    <Github className="w-4 h-4" /> Code
                  </a>
                </div>
              </div>
            ))}
          </motion.div>
        </div>

        <button
          onClick={nextSlide}
          className="absolute -right-5 top-1/2 z-10 -translate-y-1/2 bg-gray-200 dark:bg-gray-700 p-2 rounded-full hover:scale-110 transition disabled:opacity-40"
          disabled={index >= projects.length - visibleCount}
        >
          <ChevronRight className="w-5 h-5" />
        </button>
      </div>
    </section>
  );
}
