import { motion } from 'framer-motion';
import { Card, CardContent } from '@mui/material';
import { Github, ExternalLink } from 'lucide-react';

const projects = [
  { title: "Portfolio Website", description: "My personal portfolio built with React, Tailwind, Framer Motion.", image: "/images/portfolio.png", github: "https://github.com/yourusername/portfolio", demo: "https://yourportfolio.com" },
  { title: "E-commerce App", description: "Full-stack shopping app with cart and checkout.", image: "/images/ecommerce.png", github: "https://github.com/yourusername/ecommerce", demo: "https://ecommerce-demo.com" },
  { title: "Chat Application", description: "Real-time chat app using WebSockets and Node.js backend.", image: "/images/chat.png", github: "https://github.com/yourusername/chat-app", demo: "https://chat-demo.com" },
];

export default function Projects() {
  return (
    <motion.section id="projects" initial={{opacity:0,y:20}} whileInView={{opacity:1,y:0}} viewport={{once:true}} transition={{duration:0.6}}>
      <h2 className="text-3xl font-bold text-center mb-12 text-gray-900 dark:text-white">Projects</h2>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {projects.map((project,index)=>(
          <motion.div key={index} initial={{opacity:0,y:20}} whileInView={{opacity:1,y:0}} viewport={{once:true}} transition={{duration:0.5,delay:index*0.2}}>
            <Card className="rounded-2xl shadow-md hover:shadow-lg transition h-full bg-white dark:bg-gray-900">
              <CardContent className="p-6 flex flex-col h-full justify-between">
                <div>
                  <img src={project.image} alt={project.title} className="rounded-lg mb-4" />
                  <h3 className="text-xl font-semibold mb-2 text-gray-900 dark:text-white">{project.title}</h3>
                  <p className="text-gray-600 dark:text-gray-300 mb-4">{project.description}</p>
                </div>
                <div className="flex gap-4 mt-4">
                  <a href={project.github} target="_blank" rel="noopener noreferrer" className="px-3 py-2 border rounded hover:bg-gray-200 dark:hover:bg-gray-700">GitHub</a>
                  <a href={project.demo} target="_blank" rel="noopener noreferrer" className="px-3 py-2 border rounded hover:bg-gray-200 dark:hover:bg-gray-700">Demo</a>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        ))}
      </div>
    </motion.section>
  );
}
